import React, {useState, Component} from 'react';
import {render} from 'react-dom';
import Timer from './Timer';
import './ClueOuter.css';
import UserGuess from './UserGuess'; 
import ClueScore from './ClueScore';
import TimerCircle from './TimerCircle.js';

class ClueOuter extends React.Component
{
    render(){
        let clue = this.props.clue;
        let thisRoundId = clue.id;
        let userGuess = this.props.userGuess;
        let clueOuterClassName = this.props.currentRoundBeingPlayed >= thisRoundId ? "clueOuterInPlay clueOuter" : "clueOuterNotInPlay clueOuter";
        let diverClassName = this.props.currentRoundBeingPlayed == thisRoundId ? "showBlock" : "hideBlock";
        let diver2ndStyle = this.props.initializeTimers[clue.id - 1] != null ? 'none' : '';
        //console.log(clue.clue);
        return(
        <div className={clueOuterClassName}>
            <div style={roundStyle}>
                {clue.id}
            </div>

            {/* readme: this below probably should be another Component */}
            <div style={inlineBlock} name={"ClueOuterDiv" + clue.id}>
                <div style={floatLeft}>
                    {ClueInner(clue, this.props.changeUserGuess)}
                </div>
                <TimerCircle 
                    initializeTimers={this.props.initializeTimers} 
                    style={floatLeft} 
                    uniqueKey={clue.id}
                    haltTimer={clue.id < this.props.currentRoundBeingPlayed}
                    oxygenBottlesUsed={this.props.oxygenBottlesUsed}
                    currentRoundBeingPlayed={this.props.currentRoundBeingPlayed}
                    /> 
                <div style={floatLeft} className={diverClassName}>
                    <span style={{display:diver2ndStyle}}>{clue.id == 1 ? "Click To Start" : ""}</span>
                    <img src="images/diver2.png" height="80px" style={{...diverStyle,...{display:diver2ndStyle}}} onClick={(event) => this.props.onDiverClick(clue.id)}>
                    </img>                    
                </div>
                <div style={floatRight}>
                    <div style={buttonPadding}>
                        <button className="button" style={{backgroundColor:"coral"}} onClick={(event) => this.props.onClick(clue.id)}>
                            {clue.id == 7 ? "Complete!" : "Submit And Dive"}
                        </button>
                    </div>
                    <div style={buttonPadding}>
                        <button className="button" style={{backgroundColor:"antiquewhite"}} onClick={function() { alert('Resurface'); }}>
                                Resurface
                        </button>
                    </div>
                    <div style={buttonPadding}>
                        <button className="button" onClick={(event) => this.props.onClickOxygen(clue.id)}>
                                Refill O<sub>2</sub> (+10s)
                        </button>
                    </div>
                </div>
            </div>
        </div>
        );
    }
}

// readme: this was once a separate component but I was having to pass from parent to child to child and it felt messy
function ClueInner(clue, onChange){
    // console.log(clue.id);
    return (
        <div className='ClueInner' style={style}>
            <label className='ClueInnerLabel'>{clue.clue}</label>

            <UserGuess changeUserGuess={onChange} clueId={clue.id}/>  

            {/* consider making this ClueScores into another component?    */}
            <div className='ClueScores' style={textAlignLeft}>                
                {[...Array(clue.id)].map((e, i) =>  <ClueScore key={i}/>)}
            </div>

        </div>
    );
}
var textAlignLeft ={
    textAlign: 'left'
}

var diverStyle ={
    display: 'block',
    margin: 'auto'
}
var style = {
    fontFamily: 'Arial',
    margin: '10px',
    padding: '10px',
    textAlignLast: 'center'
  }
var roundStyle = {
    backgroundColor: 'steelblue',
    color: 'white',
    fontFamily: 'Arial',
    left: '10px',
    width: '20px',
    textAlign: 'center'
  }

  var styleTimer = {
    textAlign: 'center',
    backgroundColor : 'skyblue',
    width: '80px',
    padding: '5px',
    margin: 'auto',
    borderRadius: '25px'
  }

  var inlineBlock = {
    display: 'inline-block',
    width: '100%'
  }

var buttonPadding = {
    padding: '5px'
}

  var floatRight = {
    float: 'right',
    width: '33%',
    textAlign: 'center'
  }

  var floatNone = {
      float: 'none',
      width: '33%'
  }

  var floatLeft = {
    float: 'left',
    width: '33%'
  }

export default ClueOuter;
