import React, {useState, Component} from 'react';
import {render} from 'react-dom';

function Chris(){
    const [count, setCount] = useState(0);

    return (
        <div>
            <p>You clicked {count} times</p>
            <button onClick={() => setCount(count + 1)}>
            Click me
            </button>
        </div>
    )
}

export default Chris;
