render()
{
    return (       
        <div className='UserName'>
            <label  style={style}>Your name required for our records: </label>
            <input type='text' tabIndex={"0"} onBlur={(event) => this.props.changeUserName(event.target.value)}>
            </input>
            <br/>
            and then click anywhere.
        </div>  
    )
    }
}
var style = {
    fontFamily: 'Phosphate,Futura,Rockwell',
    margin: '5px 5px 0px 5px',
    padding: '5px',
    fontSize: "larger"
  }
