      fetch('http://' + this.addressOfService + '/Entries/GetWordWithClues?userName=' + this.state.userName)
          .then(res => res.json())
          .then((data) => {
            this.setState({ clues: data, isLoadingPage: false })
        })
        .catch(console.log)
      }

      render()
      {
        // let clues = GetJson();
